import { CLEAR_ORDER, CREATE_ORDER } from "../type";

export const orderReducer = (state = {}, action) => {
  switch (action.type) {
    case CREATE_ORDER:
      return {
        order: action.payload,
      };
    case CLEAR_ORDER:
      return {
        order: null,
      };
    default:
      return state;
  }
};
